import React, { useState } from "react";
import "./Products.css";
import headphonesImg from "../assets/images/headphones.jpg";
import smartwatchImg from "../assets/images/smartwatch.jpg";
import speakerImg from "../assets/images/speaker.jpg";
import mouseImg from "../assets/images/mouse.jpg";

function Products() {
  const [cart, setCart] = useState([]);

  const products = [
    {
      id: 1,
      name: "Wireless Headphones",
      price: 2499,
     image: headphonesImg,
    },
    {
      id: 2,
      name: "Smart Watch",
      price: 3999,
      image: smartwatchImg,
    },
    {
      id: 3,
      name: "Bluetooth Speaker",
      price: 1499,
      image: speakerImg,
    },
    {
      id: 4,
      name: "Gaming Mouse",
      price: 999,
      image: mouseImg,
    },
  ];

  const addToCart = (product) => {
    setCart([...cart, product]);
    alert(`${product.name} added to cart!`);
  };

  return (
    <div className="products-page">
      <h1>üõç Available Products</h1>

      <div className="product-grid">
        {products.map((product) => (
          <div className="product-card" key={product.id}>
            <img src={product.image} alt={product.name} />
            <h3>{product.name}</h3>
            <p>‚Çπ{product.price}</p>
            <button onClick={() => addToCart(product)}>Add to Cart</button>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Products;
