import React, { useState, useEffect } from "react";
import "./Admin.css";

function Admin() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [users, setUsers] = useState([]);
  const [showPasswords, setShowPasswords] = useState(false);
  const [message, setMessage] = useState("");
  const [products, setProducts] = useState([]);
  const [name, setName] = useState("");
  const [price, setPrice] = useState("");
  const [image, setImage] = useState("");


  // Admin login handler
  const handleLogin = async () => {
    try {
      const res = await fetch("http://192.168.75.150:3000/api/admin/login", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ username, password }),
      });
      const data = await res.json();
      if (res.ok) {
        setIsLoggedIn(true);
        setMessage(data.message);
        fetchUsers();
      } else {
        setMessage(data.message);
      }
    } catch (err) {
      setMessage("‚ùå Error logging in");
    }
  };

  const fetchUsers = async () => {
    const res = await fetch("http://192.168.75.150:3000/api/admin/users");
    const data = await res.json();
    setUsers(data);
  };

  const deleteUser = async (id) => {
    if (window.confirm("Are you sure you want to delete this user?")) {
      const res = await fetch(`http://192.168.75.150:3000/api/admin/users/${id}`, {
        method: "DELETE",
      });
      const data = await res.json();
      alert(data.message);
      fetchUsers();
    }
  };

  if (!isLoggedIn) {
    return (
      <div className="admin-login">
        <h2>Admin Login</h2>
        <input
          type="text"
          placeholder="Admin Username"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
        />
        <input
          type="password"
          placeholder="Admin Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />
        <button onClick={handleLogin}>Login</button>
        <p>{message}</p>
      </div>
    );
  }

  return (
    <div className="admin-dashboard">
      <h2>üëë Admin Dashboard</h2>
      <button
        className="toggle-btn"
        onClick={() => setShowPasswords(!showPasswords)}
      >
        {showPasswords ? "Hide Passwords" : "Show Passwords"}
      </button>
      <table>
        <thead>
          <tr>
            <th>ID</th>
            <th>Username</th>
            <th>Password</th>
            <th>Created At</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {users.map((user) => (
            <tr key={user.id}>
              <td>{user.id}</td>
              <td>{user.username}</td>
              <td>
                {showPasswords ? user.password : "********"}
              </td>
              <td>{new Date(user.createdAt).toLocaleString()}</td>
              <td>
                <button className="delete-btn" onClick={() => deleteUser(user.id)}>üóë Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
 useEffect(() => {
    fetchProducts();
  }, []);

  const addProduct = async () => {
    await fetch("http://192.168.75.150:3000/api/products", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ name, price, image }),
    });
    fetchProducts();
    setName("");
    setPrice("");
    setImage("");
  };

  const deleteProduct = async (id) => {
    await fetch(`http://192.168.75.150:3000/api/products/${id}`, { method: "DELETE" });
    fetchProducts();
  };

  return (
    <div className="admin-page">
      <h1>üë®‚Äçüíº Admin Dashboard</h1>

      <div className="add-product-form">
        <input value={name} onChange={(e) => setName(e.target.value)} placeholder="Product name" />
        <input value={price} onChange={(e) => setPrice(e.target.value)} placeholder="Price" type="number" />
        <input value={image} onChange={(e) => setImage(e.target.value)} placeholder="Image URL" />
        <button onClick={addProduct}>Add Product</button>
      </div>

      <h2>üì¶ Products List</h2>
      <table border="1" style={{ width: "80%", margin: "auto" }}>
        <thead>
          <tr>
            <th>ID</th><th>Name</th><th>Price</th><th>Image</th><th>Action</th>
          </tr>
        </thead>
        <tbody>
          {products.map((p) => (
            <tr key={p.id}>
              <td>{p.id}</td>
              <td>{p.name}</td>
              <td>‚Çπ{p.price}</td>
              <td><img src={p.image || "https://via.placeholder.com/50"} alt={p.name} width="50" /></td>
              <td><button onClick={() => deleteProduct(p.id)}>üóë Delete</button></td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default Admin;
