import React, { useState, useEffect } from "react";
import Navbar from "./Navbar";
import "./ChangePassword.css";

function ChangePassword() {
  const [username, setUsername] = useState("");
  const [currentPassword, setCurrentPassword] = useState("");
  const [newPassword, setNewPassword] = useState("");
  const [showCurrent, setShowCurrent] = useState(false);
  const [showNew, setShowNew] = useState(false);

  useEffect(() => {
    const storedUser = localStorage.getItem("username");
    if (storedUser) {
      setUsername(storedUser);
    } else {
      window.location.href = "/";
    }
  }, []);

  const handleChangePassword = async () => {
    try {
      const res = await fetch("http://192.168.75.150:3000/api/auth/change-password", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ username, currentPassword, newPassword }),
      });

      const data = await res.json();
      alert(data.message);

      if (data.message === "Password updated successfully") {
        setCurrentPassword("");
        setNewPassword("");
      }
    } catch (err) {
      alert("‚ùå Error: " + err.message);
    }
  };

  return (
    <div className="change-password-page">
      <Navbar />
      <div className="form-container">
        <h2>üîí Change Password</h2>
        <p className="welcome-text">Hello, <strong>{username}</strong></p>

        <div className="input-group">
          <label>Current Password</label>
          <div className="password-field">
            <input
              type={showCurrent ? "text" : "password"}
              value={currentPassword}
              onChange={(e) => setCurrentPassword(e.target.value)}
              placeholder="Enter current password"
            />
            <span onClick={() => setShowCurrent(!showCurrent)} className="toggle-icon">
              {showCurrent ? "üôà" : "üëÅÔ∏è"}
            </span>
          </div>
        </div>

        <div className="input-group">
          <label>New Password</label>
          <div className="password-field">
            <input
              type={showNew ? "text" : "password"}
              value={newPassword}
              onChange={(e) => setNewPassword(e.target.value)}
              placeholder="Enter new password"
            />
            <span onClick={() => setShowNew(!showNew)} className="toggle-icon">
              {showNew ? "üôà" : "üëÅÔ∏è"}
            </span>
          </div>
        </div>

        <button onClick={handleChangePassword}>Update Password</button>
      </div>
    </div>
  );
}

export default ChangePassword;
